mapper.select:
select distinct m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.router_query,m.permission, m.visible, m.status, m.menu_code, m.is_frame, m.menu_type, m.icon, m.sort, m.created_time from xl_menu m
AND m.menu_name like concat('%', #{menuName}, '%')
AND m.visible = #{visible}
AND m.status = #{status}
order by m.parent_id, m.sort

select distinct m.menu_id, m.parent_id, m.menu_name, m.path, m.component, m.router_query,m.permission, m.visible, m.status, m.menu_code, m.is_frame, m.menu_type, m.icon, m.sort, m.created_time from xl_menu m left join xl_role_menu rm on m.menu_id = rm.menu_id left join xl_user_role ur on rm.role_id = ur.role_id left join xl_role ro on ur.role_id = ro.role_id where ur.user_id = #{userId}
AND m.menu_name like concat('%', #{menu.menuName}, '%')
AND m.visible = #{menu.visible}
AND m.status = #{menu.status}
order by m.parent_id, m.sort

select m.menu_id from xl_menu m left join xl_role_menu rm on m.menu_id = rm.menu_id where m.deleted = 0 and rm.deleted = 0 and rm.role_id = #{roleId}
and m.menu_id not in (select m.parent_id from xl_menu m inner join xl_role_menu rm on m.menu_id = rm.menu_id and rm.role_id = #{roleId})
order by m.parent_id, m.sort
